<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"> <html> <head> <title>YixinuSHOP商城系统后台管理</title> <meta http-equiv="X-UA-Compatible" content="IE=edge" /> <meta http-equiv="Content-Type" content="text/html; charset=utf-8" /> <script src="<?php echo __ROOT__; ?>static/jquery-1.8.1.min.js" type="text/javascript"></script> <link href="<?php echo __ROOT__; ?>static/style.css" rel="stylesheet" type="text/css"/> <script src="<?php echo __ROOT__; ?>static/main.js" type="text/javascript"></script> </head> <body> <div class="area_top w100" id="area_top"> <div class="menu"> <div class="logo fleft"><a href="/" ><img src="<?php echo __ROOT__; ?>static/logo_2.png" /></a></div> <?php if(isset($menu) && is_array($menu)):foreach($menu as $key=>$v){ ?>                    <?php if($querystring == $v['url']  or $parentid == $v['id']): ?> <div class='link fleft'><a style="color:#fff; background-color: #00C1DE;" href='<?php echo __ROOT__.'?'.$v['url']; ?>'><?php echo $v['name']; ?></a> </div> <?php else: ?> <div class='link fleft'><a href='<?php echo __ROOT__.'?'.$v['url']; ?>'><?php echo $v['name']; ?></a></div> <?php endif; ?> <?php } endif; ?> <div class="link fright" ><a href="<?php echo __ROOT__; ?>?act=logout">退出登录(<?php if(isset($userinfo_name)):echo $userinfo_name; endif; ?>)</a></div> <?php if($isfounder){ ?> <div class="link fright" ><a href="javascript:void(0)" onclick='delete_cache()'>清除缓存</a></div> <?php } ?> <div class="link fright" ><a target="_blank"  href="<?php echo 'http://'.MAIN_DOMAIN; ?>">查看前台</a></div> </div> </div> <div class="area_middle w100 clearfix" id="area_middle"> <div class="area_left" id="area_left"> <?php $t = false; ?> <?php if(isset($menu_second) && is_array($menu_second)):foreach($menu_second as $key=>$v){ ?>                    <?php  if($v['g']!=$t['g'] && !empty($t) ):  ?> <div class="hr w100"></div> <?php endif; ?> <?php if($querystring == matchurl($v['url']) ): ?> <div class='menu'><a class='hover' href='<?php echo __ROOT__.'?'.$v['url']; ?>'> <font><?php if(isset($v['name'])):echo $v['name']; endif; ?></font></a></div> <?php else: ?> <div class='menu'><a href='<?php echo __ROOT__.'?'.$v['url']; ?>'> <font><?php if(isset($v['name'])):echo $v['name']; endif; ?></font></a></div> <?php endif; ?> <?php $t =$v; ?> <?php } endif; ?> </div> <div class="area_right" id="area_right"> <div class="content"> <div class="content_title w100 clearfix"> <a href="<?php echo __ROOT__; ?>?goods=spec"><img src="<?php echo __ROOT__; ?>static/icon/a1.png" /><font>列表</font></a> <a href="<?php echo __ROOT__; ?>?goods=spec&type=add"><img src="<?php echo __ROOT__; ?>static/icon/a6.png" /><font>添加</font></a> <a href="<?php echo __ROOT__; ?>"><img src="<?php echo __ROOT__; ?>static/icon/a9.png" /><font>搜索</font></a> </div> <div class="w100"> <?php if(isset($type) && ($type=='add' ||$type=='edit' ) ): ?> <form method="post" action="" name="iform" onsubmit="return icheck();" > <table class="w100 content_list"> <tr> <th width="180" style="text-align: left;">编辑 － 商品规格</th> <th width="300"></th> <th></th> </tr> <tr> <td style="text-align: right;"><span class="cr">*</span>&nbsp;<span class="fb">名称：</span></td> <td><input type="text" class="input1" name="name"  onblur="ch.onecheck(this);" value="<?php if(isset($info['name'])):echo $info['name']; endif; ?>" /></td> <td><span id="nametips" class="c1">1 到 30个字符以内的名称</span></td> </tr> <tr> <td style="text-align: right;vertical-align: top;"><span class="cr">*</span>&nbsp;<span class="fb">规格值：</span></td> <td> <textarea  onblur="ch.onecheck(this);" class='textarea1' name="specValue" ><?php if(isset($info['specValue'])):echo $info['specValue']; endif; ?></textarea> </td> <td><span id="specValuetips" class="c1">1到200个字符以内</span>&nbsp;&nbsp;<span class="c1">一行代表一个值</span></td> </tr> <tr> <td style="text-align: right;"><span class="fb">状态：</span></td> <td> <select name="status"> <option value="1" <?php if(isset($info['status']) && $info['status']==1): ?>selected='selected'<?php endif; ?> >可用</option> <option value="2" <?php if(isset($info['status']) && $info['status']==2): ?>selected='selected'<?php endif; ?> >禁用</option> </select> </td> <td><span class="c1">商品规格状态</span></td> </tr> <tr> <td style="text-align: right;"><span class="fb">说明：</span></td> <td><input type="text" class="input1" name="comments"  onblur="ch.onecheck(this);" value="<?php if(isset($info['comments'])):echo $info['comments']; endif; ?>" /></td> <td><span id="commentstips" class="c1">1 到 30个字符以内</span></td> </tr> <tr> <td></td> <td> <input type="submit" class="bt1" name="_submit_" /> </td> <td><span class="c1"></span></td> </tr> </table> </form> <script> $(function(){ var option = new Array(); option[0] = {name:'name',type:'str30',msg:'名称应该为1-30个字符！'}; option[1] = {name:'specValue',type:'str200',msg:'规格值应该为1-200个字符！'}; option[2] = {name:'comments',type:'regexp',msg:'1 到 30个字符以内！',rule:/^.{1,30}$/,required:2};  ch = new formCheck(option); }); function icheck(){ var bool_ = false; var res = ch.start(); if(res==1){ var attrValue = $("textarea[name='specValue']").attr('value'); if(attrValue==''){ confirm_('规格值列表不能为空'); return false }else{ var rule = new RegExp(/^([^\\?/&_|]+|\r|\n){0,200}$/g); if(!rule.test(attrValue)){ confirm_('规格值不能使用  /  ?  & _ | \ 等符号。'); return false } } bool_ = true; } return bool_; } </script> <?php else: ?> <table class="content_list w100"> <tr> <th style="width:110px;" >名称(说明)</th> <th style="width:30px;" >状态</th> <th>值</th> <th width="80">操作</th> </tr> <?php if(isset($list) && is_array($list)):foreach($list as $key=>$v){ ?>            <tr> <td><?php if(isset($v['name'])):echo $v['name']; endif; ?>(<span class="c1" ><?php if(isset($v['comments'])):echo $v['comments']; endif; ?></span>)</td> <td> <?php if($v['status']==1): ?> <font class='cg'>可用</font> <?php else: ?> <font class='cr'>禁用</font> <?php endif; ?> </td> <td><a><?php echo str_replace(array("\r\n", "\n", "\r"),"</a>&nbsp;<a>",$v['specValue']); ?></a></td> <td> <a href="<?php echo __ROOT__; ?>?goods=spec&type=edit&id=<?php if(isset($v['id'])):echo $v['id']; endif; ?>">修改</a>&nbsp;&nbsp; <a href="javascript:confirm_('你确定要删除规格 [<?php if(isset($v['name'])):echo $v['name']; endif; ?>] 吗？','<?php echo __ROOT__.'?goods=spec&type=delete&id='.$v['id']; ?>')" >删除</a></td> </tr> <?php } endif; ?> </table> <div id="page" class="clearfix"><?php if(isset($pageinfo)):echo $pageinfo; endif; ?></div> <?php endif; ?> </div> </div> <div style="position: fixed; right:20px; bottom: 5px;color:#999" ><?php echo CONTROLLER_NAME.'/'.ACTION_NAME; ?></div> </div> </div> </body> </html>