menu'><a class='hover' href='<?php echo __ROOT__.'?'.$v['url']; ?>'> <font><?php if(isset($v['name'])):echo $v['name']; endif; ?></font></a></div> <?php else: ?> <div class='menu'><a href='<?php echo __ROOT__.'?'.$v['url']; ?>'> <font><?php if(isset($v['name'])):echo $v['name']; endif; ?></font></a></div> <?php endif; ?> <?php $t =$v; ?> <?php } endif; ?> </div> <div class="area_right" id="area_right"> <div class="content"> <style> #goodslisttable tr th{text-align: center;} #goodslisttable tr td{ text-align: center; } </style> <div class="content_title w100 clearfix"> <a href="<?php echo __ROOT__; ?>?jinxiaocun=k"><img src="<?php echo __ROOT__; ?>static/icon/a1.png" /><font>库存 (按数量)</font></a> <a href="<?php echo __ROOT__; ?>?jinxiaocun=k&type=inventory_nums"><img src="<?php echo __ROOT__; ?>static/icon/a1.png" /><font>库存 (按批次)</font></a> <a href="<?php echo __ROOT__; ?>?jinxiaocun=k&type=warehouse"><img src="<?php echo __ROOT__; ?>static/icon/a1.png" /><font>仓库</font></a> <a href="<?php echo __ROOT__; ?>?jinxiaocun=k&type=warehouse_add"><img src="<?php echo __ROOT__; ?>static/icon/a6.png" /><font>添加仓库</font></a> <a href="<?php echo __ROOT__; ?>?jinxiaocun=k&type=inventory_records"><img src="<?php echo __ROOT__; ?>static/icon/a1.png" /><font>出入库记录</font></a> <div class="search" > <select name="warehouse_id" > <option value="0">选择仓库</option> <?php foreach($warehouse as $v){ ?> <option value="<?php if(isset($v['id'])):echo $v['id']; endif; ?>" <?php if(@$_GET['warehouse_id']==$v['id']){ ?>selected='selected'<?php } ?> ><?php if(isset($v['name'])):echo $v['name']; endif; ?></option> <?php } ?> </select> <input type="text" class="input2" placeholder="名称或编号" name="searchval" value="<?php if(isset($_GET['searchval'])):echo $_GET['searchval']; endif; ?>" onkeyup="keydown(function(){ privateMethod.search(); })" /> &nbsp;&nbsp; <button type="button" class="bt1" onclick="privateMethod.search()" >搜索</button> </div> </div> <div class="w100"> <table id='goodslisttable' class="content_list w100"> <tr> <th width='80'>编号</th> <th>商品名称</th> <th>仓库数量</th> <th>总库存</th> <th>仓库</th> <th>操作</th> </tr> <?php if(isset($list) && is_array($list)):foreach($list as $key=>$v){ ?>            <tr <?php if($v['nums']<=0){ echo "class='cy'"; } ?> > <td><?php if(isset($v['goods_sn'])):echo $v['goods_sn']; endif; ?></td> <td> <?php if(isset($v['goods_name'])):echo $v['goods_name']; endif; ?></td> <td><?php if(isset($v['nums'])):echo $v['nums']; endif; ?></td> <td><?php if(isset($v['numbers'])):echo $v['numbers']; endif; ?></td> <td><?php if(isset($v['warehouse_name'])):echo $v['warehouse_name']; endif; ?></td> <td> <input type="hidden" name="warehouse_id" value="<?php if(isset($v['warehouse_id'])):echo $v['warehouse_id']; endif; ?>" /> <input type="hidden" name="warehouse_name" value="<?php if(isset($v['warehouse_name'])):echo $v['warehouse_name']; endif; ?>" /> <input type="hidden" name="goods_id" value="<?php if(isset($v['goods_id'])):echo $v['goods_id']; endif; ?>" /> <input type="hidden" name="goods_name" value="<?php if(isset($v['goods_name'])):echo $v['goods_name']; endif; ?>" /> <a href="#" onclick="privateMethod.baosun_show_win(this)">报损</a>&nbsp;&nbsp; <a href="<?php echo __ROOT__; ?>?jinxiaocun=k&type=inventory_records&goods_id=<?php if(isset($v['goods_id'])):echo $v['goods_id']; endif; ?>" title='出入库记录' >记录</a>&nbsp;&nbsp; <a href="<?php echo __ROOT__; ?>?jinxiaocun=k&type=a_sales_record&goods_id=<?php if(isset($v['goods_id'])):echo $v['goods_id']; endif; ?>" title='销售详情' >销售详情</a> </td> </tr> <?php } endif; ?> </table> <div id="page" class="clearfix"><?php if(isset($pageinfo)):echo $pageinfo; endif; ?></div> </div> <script> $(function(){ $("input[name='searchval']").focus(); }); privateMethod = {}; privateMethod.search = function(){ var data = $(".search input,select").serialize(); var url = '/?jinxiaocun=k&type=inventory&' + data; window.location.replace(url); }; var option = new Array(); option[0] = {name: 'nums', type: 'regexp', msg: '填写一个小于总库存的数值 ', rule: /^[0-9]{1,7}$/, required: 1}; option[1] = {name: 'comments', type: 'regexp', msg: '1到120个字符，不能包括： < > \ = / . ? \'  | & ', rule: /^[^<>\\=/.?+'|&]{1,130}$/, required: 1}; ch = new formCheck(option); privateMethod.baosun_show_win = function(e){ var w_id = $(e).parent().find("input[name='warehouse_id']").val(); var w_name = $(e).parent().find("input[name='warehouse_name']").val(); var goods_id = $(e).parent().find("input[name='goods_id']").val(); var goods_name = $(e).parent().find("input[name='goods_name']").val(); $.get('/?c=jinxiaocun&a=baosun_show_win',{w_id:w_id,w_name:w_name,goods_id:goods_id,goods_name:goods_name},function(d){ var msg = kmsg(d,'报损 - 记录商品损耗',1,550,480,null,1,function(){ var res = ch.start(); if (res == 1) { var url = $("#baosun_form1").attr('action'); var data = $("#baosun_form1").serialize(); $.post(url,data,function(d){ if(d.status==1){ window.location.reload(); }else{ confirm_('<p>&nbsp;&nbsp;&nbsp;&nbsp;'+d.msg+'</p>'); } },'json'); } }); }); }; </script> </div> <div style="position: fixed; right:20px; bottom: 5px;color:#999" ><?php echo CONTROLLER_NAME.'/'.ACTION_NAME; ?></div> </div> </div> </body> </html>