php echo __ROOT__; ?>?article=field&type=edit&id=<?php if(isset($v['id'])):echo $v['id']; endif; ?>">修改</a>&nbsp;&nbsp; <a href="javascript:confirm_('删除该字段会丢失已经保存的数据<br />你确定要删除字段 [<?php if(isset($v['name'])):echo $v['name']; endif; ?>] 吗？','<?php echo __ROOT__.'?article=field&type=delete&id='.$v['id']; ?>')" >删除</a></td> </tr> <?php } endif; ?> </table> <div id="page" class="clearfix"><?php if(isset($pageinfo)):echo $pageinfo; endif; ?></div> </div> </div> <div style="position: fixed; right:20px; bottom: 5px;color:#999" ><?php echo CONTROLLER_NAME.'/'.ACTION_NAME; ?></div> </div> </div> </body> </html>