<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"> <html> <head> <title>YixinuSHOP商城系统后台管理</title> <meta http-equiv="X-UA-Compatible" content="IE=edge" /> <meta http-equiv="Content-Type" content="text/html; charset=utf-8" /> <script src="<?php echo __ROOT__; ?>static/jquery-1.8.1.min.js" type="text/javascript"></script> <link href="<?php echo __ROOT__; ?>static/style.css" rel="stylesheet" type="text/css"/> <script src="<?php echo __ROOT__; ?>static/main.js" type="text/javascript"></script> </head> <body> <div class="area_top w100" id="area_top"> <div class="menu"> <div class="logo fleft"><a href="/" ><img src="<?php echo __ROOT__; ?>static/logo_2.png" /></a></div> <?php if(isset($menu) && is_array($menu)):foreach($menu as $key=>$v){ ?>                    <?php if($querystring == $v['url']  or $parentid == $v['id']): ?> <div class='link fleft'><a style="color:#fff; background-color: #00C1DE;" href='<?php echo __ROOT__.'?'.$v['url']; ?>'><?php echo $v['name']; ?></a> </div> <?php else: ?> <div class='link fleft'><a href='<?php echo __ROOT__.'?'.$v['url']; ?>'><?php echo $v['name']; ?></a></div> <?php endif; ?> <?php } endif; ?> <div class="link fright" ><a href="<?php echo __ROOT__; ?>?act=logout">退出登录(<?php if(isset($userinfo_name)):echo $userinfo_name; endif; ?>)</a></div> <?php if($isfounder){ ?> <div class="link fright" ><a href="javascript:void(0)" onclick='delete_cache()'>清除缓存</a></div> <?php } ?> <div class="link fright" ><a target="_blank"  href="<?php echo 'http://'.MAIN_DOMAIN; ?>">查看前台</a></div> </div> </div> <div class="area_middle w100 clearfix" id="area_middle"> <div class="area_left" id="area_left"> <?php $t = false; ?> <?php if(isset($menu_second) && is_array($menu_second)):foreach($menu_second as $key=>$v){ ?>                    <?php  if($v['g']!=$t['g'] && !empty($t) ):  ?> <div class="hr w100"></div> <?php endif; ?> <?php if($querystring == matchurl($v['url']) ): ?> <div class='menu'><a class='hover' href='<?php echo __ROOT__.'?'.$v['url']; ?>'> <font><?php if(isset($v['name'])):echo $v['name']; endif; ?></font></a></div> <?php else: ?> <div class='menu'><a href='<?php echo __ROOT__.'?'.$v['url']; ?>'> <font><?php if(isset($v['name'])):echo $v['name']; endif; ?></font></a></div> <?php endif; ?> <?php $t =$v; ?> <?php } endif; ?> </div> <div class="area_right" id="area_right"> <div class="content"> <div class="content_title w100 clearfix"> <a href="<?php echo __ROOT__; ?>?goods=rollimage"><img src="<?php echo __ROOT__; ?>static/icon/a1.png" /><font>列表</font></a> <a href="<?php echo __ROOT__; ?>?goods=rollimage&type=edit"><img src="<?php echo __ROOT__; ?>static/icon/papercheck32.png" /><font>添加</font></a> <a href="javascript:privateMethod.delete()"><img src="<?php echo __ROOT__; ?>static/icon/recycle32.png" /><font>删除</font></a> </div> <div class="w100"> <table id="goodslisttable" class="content_list w100"> <tr> <th style="width:10px;"><input type="checkbox" onclick="privateMethod.listcheck(this)" /></th> <th>编号</th> <th>标签</th> <th>标题</th> <th style="width:80px;">状态</th> <th style="width:180px;">操作</th> </tr> <?php if(isset($list) && is_array($list)):foreach($list as $key=>$v){ ?>        <tr style="border-top:1px solid #93D8FF;" > <td><input type="checkbox" name="goodslistcheck[]" value="<?php if(isset($v['id'])):echo $v['id']; endif; ?>" /></td> <td><?php if(isset($v['id'])):echo $v['id']; endif; ?></td> <td><?php if(isset($v['mark'])):echo $v['mark']; endif; ?></td> <td><font class="cf60" ><?php if(isset($v['title'])):echo $v['title']; endif; ?></font></td> <td> <?php  switch($v['status']): ?><?php case '1': ?><font class='cg'>可用&nbsp;</font><?php break; ?><?php case '2': ?><font class='cr'>禁用&nbsp;</font><?php break; ?>      <?php endswitch; ?> </td> <td> <a href="<?php echo __ROOT__; ?>?goods=rollimage&type=dataedit&id=<?php if(isset($v['id'])):echo $v['id']; endif; ?>">图片编辑</a>&nbsp;&nbsp; <a href="<?php echo __ROOT__; ?>?goods=rollimage&type=edit&id=<?php if(isset($v['id'])):echo $v['id']; endif; ?>">修改</a>&nbsp;&nbsp; </td> </tr> <?php } endif; ?> </table> <div id="page" class="clearfix"><?php if(isset($pageinfo)):echo $pageinfo; endif; ?></div> </div> <script> privateMethod = {}; privateMethod.listcheck = function(e){ var chk = 'goodslistcheck'; var elems = document.getElementsByTagName("INPUT"); for (var i=0; i < elems.length; i++){ if (elems[i].name == chk || elems[i].name == chk + "[]") { elems[i].checked = e.checked; } } }; privateMethod.check = function(){ var str=$("input[name='goodslistcheck[]']:checked").serialize(); if(str==''){ return false; }else{ return str; } }; privateMethod.delete = function(){ var data = privateMethod.check(); if(data==false){ confirm_('请选择要删除的数据！'); return false; }else{ var msg2 = new message(); msg2.show('你确定要删除选择的数据吗？','警告！',null,320,90,null,1,function(){  $.ajax({ url:'?c=goods&a=rollimagdelete', type:'get', data:data, dataType:'json', success:function(d){ window.location.reload(); } }); }); } }; </script> </div> <div style="position: fixed; right:20px; bottom: 5px;color:#999" ><?php echo CONTROLLER_NAME.'/'.ACTION_NAME; ?></div> </div> </div> </body> </html>