<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"> <html> <head> <title>YixinuSHOP商城系统后台管理</title> <meta http-equiv="X-UA-Compatible" content="IE=edge" /> <meta http-equiv="Content-Type" content="text/html; charset=utf-8" /> <script src="<?php echo __ROOT__; ?>static/jquery-1.8.1.min.js" type="text/javascript"></script> <link href="<?php echo __ROOT__; ?>static/style.css" rel="stylesheet" type="text/css"/> <script src="<?php echo __ROOT__; ?>static/main.js" type="text/javascript"></script> </head> <body> <div class="area_top w100" id="area_top"> <div class="menu"> <div class="logo fleft"><a href="/" ><img src="<?php echo __ROOT__; ?>static/logo_2.png" /></a></div> <?php if(isset($menu) && is_array($menu)):foreach($menu as $key=>$v){ ?>                    <?php if($querystring == $v['url']  or $parentid == $v['id']): ?> <div class='link fleft'><a style="color:#fff; background-color: #00C1DE;" href='<?php echo __ROOT__.'?'.$v['url']; ?>'><?php echo $v['name']; ?></a> </div> <?php else: ?> <div class='link fleft'><a href='<?php echo __ROOT__.'?'.$v['url']; ?>'><?php echo $v['name']; ?></a></div> <?php endif; ?> <?php } endif; ?> <div class="link fright" ><a href="<?php echo __ROOT__; ?>?act=logout">退出登录(<?php if(isset($userinfo_name)):echo $userinfo_name; endif; ?>)</a></div> <?php if($isfounder){ ?> <div class="link fright" ><a href="javascript:void(0)" onclick='delete_cache()'>清除缓存</a></div> <?php } ?> <div class="link fright" ><a target="_blank"  href="<?php echo 'http://'.MAIN_DOMAIN; ?>">查看前台</a></div> </div> </div> <div class="area_middle w100 clearfix" id="area_middle"> <div class="area_left" id="area_left"> <?php $t = false; ?> <?php if(isset($menu_second) && is_array($menu_second)):foreach($menu_second as $key=>$v){ ?>                    <?php  if($v['g']!=$t['g'] && !empty($t) ):  ?> <div class="hr w100"></div> <?php endif; ?> <?php if($querystring == matchurl($v['url']) ): ?> <div class='menu'><a class='hover' href='<?php echo __ROOT__.'?'.$v['url']; ?>'> <font><?php if(isset($v['name'])):echo $v['name']; endif; ?></font></a></div> <?php else: ?> <div class='menu'><a href='<?php echo __ROOT__.'?'.$v['url']; ?>'> <font><?php if(isset($v['name'])):echo $v['name']; endif; ?></font></a></div> <?php endif; ?> <?php $t =$v; ?> <?php } endif; ?> </div> <div class="area_right" id="area_right"> <div class="content"> <style> #goodslisttable tr th{text-align: center;} #goodslisttable tr td{border-bottom: 0px dashed #ECE7E7; text-align: center; padding: 14px 5px;} </style> <div class="content_title w100 clearfix"> <a href="<?php echo __ROOT__; ?>?goods=list"><img src="<?php echo __ROOT__; ?>static/icon/a1.png" /><font>列表(全部)</font></a> <a href="<?php echo __ROOT__; ?>?goods=list&numbers=1"><img src="<?php echo __ROOT__; ?>static/icon/a1.png" /><font>列表(库存不足)</font></a> <a href="javascript:void(0)" onclick="goodsfun.setonline(1)"><img src="<?php echo __ROOT__; ?>static/icon/papercheck32.png" /><font>上架</font></a> <a href="javascript:void(0)" onclick="goodsfun.setonline(2)"><img src="<?php echo __ROOT__; ?>static/icon/block32.png" /><font>下架</font></a> <a href="javascript:void(0)" onclick="goodsfun.deleteGoods()"><img src="<?php echo __ROOT__; ?>static/icon/recycle32.png" /><font>删除</font></a> <div class="search" > <span>名称：</span> <input type="text" class="input2" name="goodsname" value="<?php if(isset($_GET['goodsname'])):echo $_GET['goodsname']; endif; ?>" onkeyup="keydown(function(){ privateMethod.search(); })" /> <span>分类：</span> <select name="cateid"> <option value="0" >请选择</option> <?php if(isset($categroy_list) && is_array($categroy_list)):foreach($categroy_list as $key=>$v){ ?>            <option value="<?php if(isset($v['id'])):echo $v['id']; endif; ?>" <?php if(@$_GET['cateid']==$v['id']){ ?>selected='selected'<?php } ?> ><?php if(isset($v['name'])):echo $v['name']; endif; ?></option> <?php } endif; ?> </select> &nbsp;&nbsp; <input type="button" class="bt1" onclick="privateMethod.search()" value="搜索" /> </div> </div> <div class="w100"> <table id="goodslisttable" class="content_list w100"> <tr> <th style="width:15px;"><input type="checkbox" onclick="goodsfun.listcheck(this)" /></th> <th>商户</th> <th>编号</th> <th>名称</th> <th>价格</th> <th>总库存</th> <th>类型</th> <th style="width:80px;">状态</th> <th style="width:80px;">操作</th> </tr> <?php $tempId = null; $tmpvar = null; ?> <?php if(isset($list) && is_array($list)):foreach($list as $key=>$v){ ?>            <?php  if( $tmpvar!=$v['business_no'] ){ $color = printColor();  } ?> <tr style=" <?php if($tempId==$v['id']){ ?> color:<?php if(isset($color)):echo $color; endif; ?>; <?php }else{ ?> color:<?php if(isset($color)):echo $color; endif; ?>;border-top:1px solid #DADADA; <?php } ?>" > <td><input type="checkbox" name="goodslistcheck[]" value="<?php if(isset($v['gid'])):echo $v['gid']; endif; ?>" /></td> <td><?php if(isset($v['business_name'])):echo $v['business_name']; endif; ?></td> <td><?php if(isset($v['sn2'])):echo $v['sn2']; endif; ?></td> <td><?php if(isset($v['goodsName'])):echo $v['goodsName']; endif; ?>  , <?php if( empty($v['attributeStr']) ): echo '其它规格'; else: echo $v['attributeStr']; endif; ?></td> <td><?php if(isset($v['shopPrice'])):echo $v['shopPrice']; endif; ?></td> <td><?php if($v['numbers']<=0){ ?><font class='cr fb'><?php if(isset($v['numbers'])):echo $v['numbers']; endif; ?></font><?php }else{ echo $v['numbers']; } ?></td> <td><?php if(isset($v['cateName'])):echo $v['cateName']; endif; ?></td> <td> <?php  switch($v['addStatus']): ?><?php case '1': ?><font class='cg'>销售中…&nbsp;</font><?php break; ?><?php case '2': ?><font class='cr'>已下架&nbsp;</font><?php break; ?>      <?php endswitch; ?> </td> <td> <?php  if($tempId==$v['id']):  else:  ?> <a href="<?php echo __ROOT__; ?>?goods=edit&id=<?php if(isset($v['id'])):echo $v['id']; endif; ?><?php if( isset($_GET['p']) ): echo '&p='.$_GET['p']; endif; ?>">修改</a>&nbsp;&nbsp; <a href="javascript:confirm_('你确定要删除商品[ <?php if(isset($v['goodsName'])):echo $v['goodsName']; endif; ?> ] 吗？','<?php echo __ROOT__; ?>?c=goods&a=goodsdelete&id=<?php if(isset($v['id'])):echo $v['id']; endif; ?><?php if( isset($_GET['p']) ): echo '&p='.$_GET['p']; endif; ?>')" >删除</a> <?php endif; ?> </td> </tr> <?php $tempId = $v['id']; $tmpvar = $v['business_no']; ?> <?php } endif; ?> </table> <div id="page" class="clearfix"><?php if(isset($pageinfo)):echo $pageinfo; endif; ?></div> <script> $(function(){ $("input[name='goodsname']").focus(); }); goodsfun = {}; goodsfun.listcheck = function(e){ var chk = 'goodslistcheck'; var elems = document.getElementsByTagName("INPUT"); for (var i=0; i < elems.length; i++){ if (elems[i].name == chk || elems[i].name == chk + "[]") { elems[i].checked = e.checked; } } }; goodsfun.check = function(){ var str=$("input[name='goodslistcheck[]']:checked").serialize(); if(str==''){ return false; }else{ return str; } }; goodsfun.deleteGoods = function(){ var data = goodsfun.check(); if(data==false){ confirm_('请选择要删除的商品！'); return false; }else{ var msg2 = new message(); msg2.show('你确定要删除选择的商品吗？','警告！',null,320,90,null,1,function(){  $.ajax({ url:'?c=goods&a=goodsdelete', type:'get', data:data, dataType:'json', success:function(d){ window.location.reload(); } }); }); } }; goodsfun.setonline = function(s){ var data = goodsfun.check(); if(data==false){ confirm_('请选择商品！'); return false; }else{ confirm_('正在设置……'); $.ajax({ url:'?c=goods&a=setonline&status='+s, type:'get', data:data, dataType:'json', success:function(d){ window.location.reload(); } }); } }; privateMethod = {}; privateMethod.search = function(){ var data = ''; var goodsname = $("input[name='goodsname']").attr('value'); if(goodsname!=''){ data += '&goodsname='+goodsname; } var cateid = $("select[name='cateid']").attr('value'); if(cateid!=0){ data += '&cateid='+cateid; } var url = '/?goods=list' + data; window.location.replace(url); } </script> </div> </div> <div style="position: fixed; right:20px; bottom: 5px;color:#999" ><?php echo CONTROLLER_NAME.'/'.ACTION_NAME; ?></div> </div> </div> </body> </html>